export const initialState = {
    quotes: [],
    isFetching: null,
    error:""
}

const reducer = (state = initialState, action) => {
    switch(action.type){
        case FETCH_QUOTES_SUCCESS:
            return({
                ...state,
                quotes: action.payload
            })
        case FETCH_QUOTES_FAIL:
            return({
                ...state,
                error: action.payload
            })
        default:
            return state;
    }
}

export default reducer;
